/usr/local/bin/gpustat --color -cupFP --gpuname-width 30 && \
echo -e "user(`who | cut -d ' ' -f 1 | sort -u | wc -l`)\tcpu(`LC_ALL=C top -bn1 | grep "Cpu(s)" | sed "s/.*, *\([0-9.]*\)%* id.*/\1/" | awk '{print 100 - $1}' | awk '{ printf("%3.1f%%", $1) }'`)\tgpu`gpucmd=$(echo "$(nvidia-smi)" | sed -nr 's/.*\s([0-9]+)%.*/\1/p');gpucnt=$(echo "$gpucmd" | wc -l);gputot=$(echo "$gpucmd" | awk '{count+=$1} END {print count}');echo "$gputot $gpucnt" | awk -v format="(%3.1f%%)" '{printf format, $1/$2}'`\tmem(`free -m | awk '/Mem:/ { printf("%3.1f%%", $3/$2*100) }'`)\thdd(`df -h / | awk '/\// {print $(NF-1)}'`)"